import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from "react-router-dom";
import '../styles/Profile.css';


const Profile = () => {
    const [fullName, setFullName] = useState('');
    const [events, setEvents] = useState([{
        name: "החתונה של הדר ואביחי",
        date: "21/03/2023",
        location: "Agadata"
    }]);
    const [showModal, setShowModal] = useState(false);
    const [newEvent, setNewEvent] = useState({
        name: '',
        location: '',
        date: ''
});

    const navigate = useNavigate();

    // טוען את המידע של המשתמש מה-localStorage
    useEffect(() => {

    }, []);

    // פונקציה לבדיקת אם כל השדות מלאים
    const isFormComplete = () => {
        return newEvent.name && newEvent.location && newEvent.date;
    };



    // פונקציה להוסיף אירוע
    const handleAddEvent = () => {

    };


    // פונקציה לעדכון השדות של האירוע
    const handleInputChange = (e) => {
        setNewEvent({ ...newEvent, [e.target.name]: e.target.value });
    };

    //פונקציה למעבר לדף האירוע
    const handleEventClick = (event) => {
        navigate(`/event/${event.name}-${event.location}`, { state: { event } });
    };


    return (
        <div className={"main-container"}>
            <div className={"profile-header-container"}>

            </div>
            <button id={"create-event"} className={"event-button"} onClick={() => setShowModal(true)}>Create Event</button>
            <div className="event-container">
                <div className={"event-container-title"}>MY EVENTS</div>
                <div className={"event-list"}>
                    <div className={"event-list-header"}>
                        <span>Event Name</span>
                        <span>Date</span>
                        <span>Location</span>
                    </div>
                    {
                        events.map((event) => {
                            return (
                                <div className={"event-item"}>
                                    <span>{event.name}</span>
                                    <span>{event.date}</span>
                                    <span>{event.location}</span>
                                </div>
                            );
                        })
                    }
                </div>

            </div>
            {showModal && (
                <div className="modal-event">
                    <div className="modal-content">
                        <h3>Create New Event</h3>
                        <input
                            className={"modal-input"}
                            type="text"
                            name="name"
                            placeholder="Event Name"
                            value={newEvent.name}
                            onChange={handleInputChange}
                        />
                        <input
                            className={"modal-input"}
                            type="text"
                            name="location"
                            placeholder="Location"
                            value={newEvent.location}
                            onChange={handleInputChange}
                        />
                        <input
                            className={"modal-input"}
                            type="date"
                            name="date"
                            value={newEvent.date}
                            onChange={handleInputChange}
                        />
                        <button className={"event-button"}
                            onClick={handleAddEvent}
                            disabled={!isFormComplete()}
                        >
                            Create
                        </button>
                        <button className={"event-button"} onClick={() => setShowModal(false)}>Close</button>
                    </div>
                </div>
            )}

        </div>
    );
};

export default Profile;
