import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Home from './components/Home';
import Login from './components/Login';
import AboutUs from './components/AboutUs';
import Packages from './components/Packages';
import PurchasePackages from './components/PurchasePackages';
import Register from './components/Register';
import Profile from './components/Profile';
import EventPage from './components/EventPage';
import Navbar from "./components/Navbar";
import Cookies from 'js-cookie';

class App extends React.Component{

    state = {
        isLoggedIn: false
    }

    componentDidMount() {
        this.getCookies()
    }

    getCookies = ()=>{
        const token = Cookies.get('token'); // גישה לקוקי בשם 'token'
        if (token) {
            this.updateLogged(true)
        } else {
            this.updateLogged(false)
        }
    }

    updateLogged = (update) =>{
        this.setState({
            isLoggedIn: update
        });
    }

    loggOut = () =>{
        
    }

    render() {
        return (
            <Router>
                <Navbar updateLogged={this.updateLogged} logged={this.state.isLoggedIn}/>
                <Routes>
                    <Route path="/" element={<Home />} />
                    <Route path="/login" element={<Login updateLogged={this.updateLogged} />} />
                    <Route path="/about-us" element={<AboutUs />} />
                    <Route path="/packages" element={<Packages />} />
                    <Route path="/purchase-packages" element={<PurchasePackages />} />
                    <Route path="/profile" element={<Profile />} />
                    <Route path="/event/:id" element={<EventPage />} />

                </Routes>
            </Router>
        )
    }
}

export default App;
