import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../styles/EventPage.css';

function EventPage({ event }) {
    const [uploadProgress, setUploadProgress] = useState(0);
    const [images, setImages] = useState([]);
    const [previewImages, setPreviewImages] = useState([]);
    const [message, setMessage] = useState("");
    const [qrImage, setQrImage] = useState(null);
    const [editMode, setEditMode] = useState(false);

    useEffect(() => {
        setUploadProgress(0);
        setQrImage(null);
        setMessage("");
        setImages([]);
        setPreviewImages([]);
    }, [event]);

    // שמירת הקבצים שנבחרו
    const handleFileSelect = (e) => {
        const selectedFiles = Array.from(e.target.files);
        setImages(selectedFiles);
        const imagePreviews = selectedFiles.map((file) => URL.createObjectURL(file));
        setPreviewImages(imagePreviews);
        setUploadProgress(0);
    };

    // תהליך ההעלאה לשרת
    const handleSubmit = () => {
        const formData = new FormData();
        images.forEach((image, index) => {
            formData.append(`image${index}`, image);
        });

        axios.post(`http://localhost:8000/add-photos/?event-id=${event.id}`, formData, {
            headers: {
                'Content-Type': 'multipart/form-data'
            },
            onUploadProgress: (progressEvent) => {
                const percentCompleted = Math.round((progressEvent.loaded * 100) / progressEvent.total);
                setUploadProgress(percentCompleted);
            }
        })
            .then(response => {
                if (response.data.status === "success") {
                    setImages([]);
                    setMessage("התמונות עודכנו בהצלחה!");
                    setUploadProgress(0);
                } else {
                    setMessage("לא הצלחנו להעלות את התמונות. אנא נסה שנית!");
                }
                setTimeout(() => setMessage(""), 2000);
            })
            .catch(error => {
                console.error("There was an error uploading the images!", error);
                setMessage("אירעה שגיאה בהעלאה. אנא נסה שנית.");
            });
    };

    const handleQr = () => {
        axios.get(`http://localhost:8000/get-qr?event-id=${event.id}`, { responseType: 'blob' })
            .then((response) => {
                const qrImageUrl = URL.createObjectURL(response.data);
                setQrImage(qrImageUrl);
            })
            .catch((error) => {
                console.error("Error fetching QR code:", error);
            });
    };

    const closeQr = () => {
        setQrImage(null);
    };

    const openEditMode = () => {
        setEditMode(true);
    };

    const closeEditMode = () => {
        setEditMode(false);
    };

    const removeImage = (index) => {
        const newImages = images.filter((_, i) => i !== index);
        const newPreviews = previewImages.filter((_, i) => i !== index);
        setImages(newImages);
        setPreviewImages(newPreviews);
    };

    return (
        <div className={"event-page-container"}>
            <div className={"event-page-title"}>
                <h1 className="event-name">{event.name}</h1>
                <div className="event-details">
                    <span className="event-date">{event.date}</span>
                    <span style={{ fontWeight: "bold" }}> · </span>
                    <span className="event-location">{event.location}</span>
                </div>
                <button id={"qr-button"} className="event-button" onClick={handleQr}>QR</button>
            </div>
            <div className={"event-page-body"}>
                <h3>Upload Photos</h3>
                <input
                    type="file"
                    className={"upload-input"}
                    onChange={handleFileSelect}
                    multiple
                />
                <button
                    className={"edit-button"}
                    onClick={openEditMode}
                    disabled={images.length === 0}
                >
                    Edit
                </button>

                <div className="progress-bar">
                    <div className="progress" style={{ width: `${uploadProgress}%` }}></div>
                </div>
                <button
                    className={"upload-button"}
                    onClick={handleSubmit}
                    disabled={images.length === 0 || uploadProgress === 100}
                >
                    Upload
                </button>
                {message}
            </div>
            {qrImage && (
                <div className="qr-code-popup">
                    <span className="close" onClick={closeQr}>&times;</span>
                    <img src={qrImage} alt="QR Code" className="qr-image" />
                    <a href={qrImage} download={`${event.name}_qr_code.jpg`}>Download QR Code</a>
                </div>
            )}

            {editMode && (
                <div className="edit-popup">
                    <div className="edit-content">
                        <h3>Edit Selected Images</h3>
                        <div className="image-preview-container">
                            {previewImages.map((image, index) => (
                                <div key={index} className="image-preview">
                                    <img src={image} alt={`Selected ${index}`} />
                                    <button className="remove-button" onClick={() => removeImage(index)}>Remove</button>
                                </div>
                            ))}
                        </div>
                        <button className="save-button" onClick={closeEditMode}>Save</button>
                    </div>
                </div>
            )}
        </div>
    );
}

export default EventPage;
