import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const Register = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [isFormValid, setIsFormValid] = useState(false);

    useEffect(() => {
        validateForm();
    }, [email, password, confirmPassword]);

    const validateForm = () => {
        const emailValid = email.includes('@');
        const passwordValid = password.length >= 8 && /[A-Za-z]/.test(password);
        const passwordsMatch = password === confirmPassword;
        setIsFormValid(emailValid && passwordValid && passwordsMatch);
    };

    const handleEmailChange = (e) => {
        setEmail(e.target.value);
    };

    const handlePasswordChange = (e) => {
        setPassword(e.target.value);
    };

    const handleConfirmPasswordChange = (e) => {
        setConfirmPassword(e.target.value);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // כאן תוכל להוסיף את הקוד ליצירת משתמש חדש
        console.log('User registered:', { email, password });
    };

    return (
        <div>
            <h2>הרשמה</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>אימייל:</label>
                    <input type="email" value={email} onChange={handleEmailChange} />
                </div>
                <div>
                    <label>סיסמה:</label>
                    <input type="password" value={password} onChange={handlePasswordChange} />
                </div>
                <div>
                    <label>אשר סיסמה:</label>
                    <input type="password" value={confirmPassword} onChange={handleConfirmPasswordChange} />
                </div>
                <button type="submit" disabled={!isFormValid}>הרשם</button>
            </form>
            <p>נרשמת כבר? <Link to="/login">התחבר</Link></p>
        </div>
    );
};

export default Register;
