import React, { useState, useEffect } from 'react';
import { Link } from "react-router-dom";

const Profile = () => {
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [images, setImages] = useState([]);
    const [events, setEvents] = useState([]);
    const [showModal, setShowModal] = useState(false);
    const [newEvent, setNewEvent] = useState({
        groom: '',
        bride: '',
        date: '',
        location: ''
    });

    useEffect(() => {
        const storedUser = JSON.parse(localStorage.getItem('user'));
        if (storedUser) {
            setFirstName(storedUser.firstName);
            setLastName(storedUser.lastName);
        }
    }, []);

    const handleAddEvent = () => {
        setEvents([...events, newEvent]);
        setNewEvent({ groom: '', bride: '', date: '', location: '' });
        setShowModal(false);
    };

    const handleInputChange = (e) => {
        setNewEvent({ ...newEvent, [e.target.name]: e.target.value });
    };

    return (
        <div>
            <h2 className={"Name-style"}>שלום {firstName} {lastName}</h2>
            <div>
                <h3 className={"Event-style"}>האירועים שלי</h3>
                <ul>
                    {events.map((event, index) => (
                        <li key={index}>
                            {event.groom} ו-{event.bride}, {event.date} ב-{event.location}
                        </li>
                    ))}
                </ul>
            </div>
            <button onClick={() => setShowModal(true)}>הוסף אירוע</button>
            {showModal && (
                <div className="modal">
                    <div className="modal-content">
                        <h3>הוסף אירוע חדש</h3>
                        <input
                            type="text"
                            name="groom"
                            placeholder="שם החתן"
                            value={newEvent.groom}
                            onChange={handleInputChange}
                        />
                        <input
                            type="text"
                            name="bride"
                            placeholder="שם הכלה"
                            value={newEvent.bride}
                            onChange={handleInputChange}
                        />
                        <input
                            type="date"
                            name="date"
                            value={newEvent.date}
                            onChange={handleInputChange}
                        />
                        <input
                            type="text"
                            name="location"
                            placeholder="מיקום"
                            value={newEvent.location}
                            onChange={handleInputChange}
                        />
                        <button onClick={handleAddEvent}>שמור</button>
                        <button onClick={() => setShowModal(false)}>סגור</button>
                    </div>
                </div>
            )}
            <p className={"Link-exit"}>רוצה לצאת? <Link to="/">התנתק</Link></p>
        </div>
    );
};

export default Profile;
