import React, {useEffect, useState} from 'react';
import axios from 'axios';
import '../styles/EventPage.css';

function EventPage({ event }) {
    const [uploadProgress, setUploadProgress] = useState(0);
    const [images, setImages] = useState([]);
    const [message, setMessage] = useState("");
    const [qrImage, setQrImage] = useState(null);


    useEffect(()=>{
        setUploadProgress(0)
        setQrImage(null)
        setMessage("")
        setImages([])
    },[event])

    // שמירת הקבצים שנבחרו
    const handleFileSelect = (e) => {
        setImages([...e.target.files]);
        setUploadProgress(0); // אפס את ההתקדמות לפני תחילת ההעלאה
    };

    // תהליך ההעלאה לשרת
    const handleSubmit = () => {
        debugger
        const formData = new FormData();
        images.forEach((image, index) => {
            formData.append(`image${index}`, image);
        });

        axios.post(`http://localhost:8000/add-photos/?event-id=${event.id}`, formData, {
            headers: {
                'Content-Type': 'multipart/form-data'
            },
            onUploadProgress: (progressEvent) => {
                const percentCompleted = Math.round((progressEvent.loaded * 100) / progressEvent.total);
                setUploadProgress(percentCompleted);
            }
        })
            .then(response => {
                if (response.data.status === "success") {
                    setImages([]);
                    setMessage("התמונות עודכנו בהצלחה!");
                    setUploadProgress(0)
                } else {
                    setMessage("לא הצלחנו להעלות את התמונות. אנא נסה שנית!");
                }
                setTimeout(() => setMessage(""), 2000);
            })
            .catch(error => {
                console.error("There was an error uploading the images!", error);
                setMessage("אירעה שגיאה בהעלאה. אנא נסה שנית.");
            });

    };

    const handleQr =() =>{
        axios.get(`http://localhost:8000/get-qr?event-id=${event.id}`, { responseType: 'blob' })
            .then((response) => {
                const qrImageUrl = URL.createObjectURL(response.data);
                setQrImage(qrImageUrl);
            })
            .catch((error) => {
                console.error("Error fetching QR code:", error);
            });
    }

    const closeQr =() =>{
        setQrImage(null)
    }

    return (
        <div className={"event-page-container"}>
            <div className={"event-page-title"}>
                <h1 className="event-name">{event.name}</h1>
                <div className="event-details">
                    <span className="event-date">{event.date}</span>
                    <span style={{ fontWeight: "bold" }}> · </span>
                    <span className="event-location">{event.location}</span>
                </div>
                <button id={"qr-button"} className="event-button" onClick={handleQr}>QR</button>
            </div>
            <div className={"event-page-body"}>
                <h3>Upload Photos</h3>
                <input
                    type="file"
                    className={"upload-input"}
                    onChange={handleFileSelect}
                    multiple
                />

                <div className="progress-bar">
                    <div className="progress" style={{ width: `${uploadProgress}%` }}></div>
                </div>
                <button
                    className={"upload-button"}
                    onClick={handleSubmit}
                    disabled={images.length ===0 || uploadProgress === 100}
                >
                    Upload
                </button>
                {message}
            </div>
            {qrImage && (
                <div className="qr-code-popup">
                    <span className="close" onClick={closeQr}>&times;</span>
                    <img src={qrImage} alt="QR Code" className="qr-image" />
                    <a href={qrImage} download={`${event.name}_qr_code.jpg`}>Download QR Code</a>
                </div>
            )}
        </div>
    );
};

export default EventPage;
